import React, { Component } from 'react';
import PropTypes from 'prop-types';
import InputAddMovie from './inputAddMovie';
import InputGenreRating from './inputGenreRating';

class AddMovie extends Component {
  constructor() {
    super();
    this.onChange = this.onChange.bind(this);
    this.onClick = this.onClick.bind(this);

    this.state = {
      subtitle: '',
      title: '',
      imagePath: '',
      storyline: '',
      rating: 0,
      genre: 'action',
    };
  }

  onClick = (event) => {
    const { onClick } = this.props;
    event.preventDefault();
    onClick(this.state);
    this.setState({
      subtitle: '',
      title: '',
      imagePath: '',
      storyline: '',
      rating: 0,
      genre: 'action',
    });
  }

  handleChange = ({ target }) => { // funcao tirada do course generica como tem arrow function nao precisa do this
    const { name, value } = target;
    this.setState({
      [name]: value, // o valor da variavel ser o nome da chave do objeto
    });
  }

  Reset = (event) => {
    event.preventDefault();
    const { onClick } = this.props;
    onClick(this.state);
    this.setState({ // resetando tudo para ficar com o estado inicial
      subtitle: '',
      title: '',
      imagePath: '',
      storyline: '',
      rating: 0,
      genre: 'action',
    });
  }

  render() {
    const { title, subtitle, storyline, rating, genre, imagePath } = this.state;
    return (
      <div>
        <form data-testid="add-movie-form">
          <InputAddMovie
            title={ title }
            subtitle={ subtitle }
            storyline={ storyline }
            imagePath={ imagePath }
            onChange={ this.onChange }
          />
          <InputGenreRating
            rating={ rating }
            genre={ genre }
            onChange={ this.onChange }
          />
          <button type="submit" data-testid="send-button" onClick={ this.onClick }>
            Adicionar filme
          </button>
        </form>
      </div>
    );
  }
}

AddMovie.propTypes = {
  onClick: PropTypes.func.isRequired,
};

export default AddMovie;
