import React, { Component } from 'react';
import SearchBar from './SearchBar';
import AddMovie from './AddMovie';
import movies from '../data';
import MovieList from './MovieList';

class MovieLibrary extends Component {
  constructor() {
    super();

    this.state = {
      searchText: '',
      bookmarkedOnly: false,
      selectedGenre: '',
      movies,
    };
  }

  filterByText= (text) => {
    const { movies } = this.state;
    const filter = movies.filter((element) => element.title.includes(text)
    || element.subtitle.includes(text)
    || element.storyline.includes(text));
    this.setState({
      movies: filter,
    });
  }

  filterByGenre = (select) => {
    const { movies } = this.state;
    const filterGenre = movies.filter((element) => element.genre === select);
    this.setState({
      movies: movies.filter((element) => element.genre === select),
    });
  }

  filterByFavorite = (check) => {
    const { movies } = this.state;
    this.setState({
      movies: movies.filter((element) => element.bookmarked === check),
    });
  };

  render() {
    const { searchText, bookmarkedOnly, selectedGenre, movies } = this.state;
    return (
      <div>
        <SearchBar
          searchText={ searchText }
          onSearchTextChange={ searchText }
          bookmarkedOnly={ bookmarkedOnly }
          onBookmarkedChange={ searchText }
          selectedGenre={ selectedGenre }
          onSelectedGenreChange={searchText }
        />
        <AddMovie onClick={ this.handleClick } />
        <MovieList movies={ movies } />
      </div>
    );
  }
}

export default MovieLibrary;
