import React, { Component } from 'react';
import SearchBar from './SearchBar';
import AddMovie from './AddMovie';
import movies from '../data';
import MovieList from './MovieList';

class MovieLibrary extends Component {
  constructor() {
    super();

    this.state = {
      searchText: '',
      bookmarkedOnly: false,
      selectedGenre: '',
      movies,
    };
  }

  onSearchTextChange = ({ target }) => {
    const listMov = movies;
    if (target.name === 'searchText') {
      this.filterByText(target.value);
    } else {
      this.setState({
        movies: listMov
      });
    }
  }

  filterByText= (text) => {
    const listMovies = movies;
    const filter = listMovies.filter((element) => element.title.includes(text)
    || element.subtitle.includes(text)
    || element.storyline.includes(text));
    this.setState({
      movies: filter,
    });
  }

  filterByGenre = (select) => {
    const { movies } = this.state;
    const filterGenre = movies.filter((element) => element.genre === select);
    this.setState({
      movies: filterGenre,
    });
  }

  filterByFavorite = (check) => {
    const { movies } = this.state;
    const filterCheck = movies.filter((element) => element.bookmarked === check);
    this.setState({
      movies: filterCheck,
    });
  };

  render() {
    const { searchText, bookmarkedOnly, selectedGenre, movies } = this.state;
    return (
      <div>
        <SearchBar
          searchText={ searchText }
          onSearchTextChange={ this.onSearchTextChange }
          bookmarkedOnly={ bookmarkedOnly }
          /* onBookmarkedChange={ this.onBookmarkedChange } */
          selectedGenre={ selectedGenre }
          /* onSelectedGenreChange={ this.onBookmarkedChange } */
        />
        <MovieList movies={ movies } />
        <AddMovie onClick={ this.handleClick } />
      </div>
    );
  }
}

export default MovieLibrary;
